import React from 'react'
import Login from './Security'
import NavBar from './navBar'
import { Switch, Route } from 'react-router-dom'

function App() {
    this.state = { idUser: '' }

    function sendUser (idUser) {
        this.setState({idUser});
    };

    return (
        <div className="Nav">
            <Login sendUser={this.sendUser} />
            { (this.state.idUser) ? <NavBar idUser={this.state.idUser} /> : null } 
            <main>
                <div className={classes.toolbar} />
                <Switch>
                    {this.routes.map((route, index) => (
                        <Route 
                            key={index}
                            path={route.path}
                            exact={route.exact}
                            children={<route.main />}
                        />
                    ))}
                    <Route path='/DataSet/:id' component={DataSet} />
                </Switch>
            </main>
        </div>
    );
}

export default App;
