import { React, Component } from 'react'
import Axios from 'axios'
import NavBar from '../navBar'

class Login extends Component {
    constructor(props) {
        super(props);

        this.state = {
            isAuthenticated: false,
            id_User: null,
            name: ''
        }

        this.handleChange = this.handleChange.bind(this);
        this.OnSubmit = this.OnSubmit.bind(this);
    }

    async Authenticate(url) {
        let user;

        await Axios.get(url)
            .then(res => { user = res.data._id; } );

        console.log(user);
        console.log(typeof(user._id))
        this.setState({ isAuthenticated: (user._id !== undefined && user._id !== null), id_User: user._id });
    }

    OnSubmit(event) {
        var url = 'http://localhost:8000';
        var endpoint = '/User/' + this.state.name

        this.Authenticate(url + endpoint);
    }

    handleChange(event) {
        this.setState({ name: event.target.value });
    }

    render() {
        return (
            <div>
                { (this.state.isAuthenticated) ? ( <NavBar idUser={this.state.id_User} /> ) :
                    (
                        <div className="Login">
                            <form onSubmit={this.OnSubmit}>
                                <label>Name:
                                    <input type="text" value={this.state.name} onChange={this.handleChange} />
                                </label>
                                <input type="submit" value="Submit" />
                            </form>
                        </div>
                    )
                }
            </div>
        );
    }
}

export default Login;
